const showPasswordToggle=document.querySelectorAll("[type='password']"),togglePasswordButtons=document.querySelectorAll(".toggle-password");showPasswordToggle.forEach(s=>{s.addEventListener("click",()=>{s.classList.add("input-password"),togglePasswordButtons.forEach(s=>{s.classList.remove("d-none")})})});const passwordInputs=document.querySelectorAll("[type='password']");togglePasswordButtons.forEach(s=>{s.addEventListener("click",()=>{togglePassword(passwordInputs,s)})});const togglePassword=(s,o)=>{s.forEach(s=>{"password"===s.type?(s.type="text",o.setAttribute("aria-label","Hide password.")):(s.type="password",o.setAttribute("aria-label","Show password as plain text. Warning: this will display your password on the screen."))})};